import React, { useState } from 'react';
import { Search, MapPin, Star, Phone, Globe, ExternalLink, Filter, Loader } from 'lucide-react';

const StarRating = ({ rating }) => {
  const stars = [];
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;

  for (let i = 0; i < 5; i++) {
    if (i < fullStars) {
      stars.push(<Star key={i} className="w-3.5 h-3.5 fill-yellow-400 text-yellow-400" />);
    } else if (i === fullStars && hasHalfStar) {
      stars.push(
        <div key={i} className="relative w-3.5 h-3.5">
          <Star className="absolute w-3.5 h-3.5 text-gray-300" />
          <div className="absolute overflow-hidden w-1/2">
            <Star className="w-3.5 h-3.5 fill-yellow-400 text-yellow-400" />
          </div>
        </div>
      );
    } else {
      stars.push(<Star key={i} className="w-3.5 h-3.5 text-gray-300" />);
    }
  }

  return (
    <div className="flex items-center gap-1">
      <div className="flex">{stars}</div>
      <span className="text-sm text-gray-600 ml-1">({rating.toFixed(1)})</span>
    </div>
  );
};

function App() {
  const [keyword, setKeyword] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [businesses, setBusinesses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [nameFilter, setNameFilter] = useState('');
  const [ratingFilter, setRatingFilter] = useState('');
  const [showFilters, setShowFilters] = useState(false);

  const apiKey = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;

  const geocodeZipCode = async (zipCode) => {
    // Simulación de geocodificación con datos de ejemplo
    const mockLocations = {
      '28001': { lat: 40.4168, lng: -3.7038 }, // Madrid
      '28792': { lat: 40.4168, lng: -3.7038 }, // Madrid área
      '08001': { lat: 41.3851, lng: 2.1734 },  // Barcelona
      '46001': { lat: 39.4699, lng: -0.3763 }, // Valencia
      '41001': { lat: 37.3891, lng: -5.9845 }, // Sevilla
      '10001': { lat: 40.7589, lng: -73.9851 }, // NYC
      '90210': { lat: 34.0901, lng: -118.4065 } // Beverly Hills
    };

    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const location = mockLocations[zipCode];
        if (location) {
          resolve(location);
        } else {
          reject(new Error(`Código postal ${zipCode} no encontrado. Prueba con: 28001, 08001, 46001, 41001, 10001, o 90210`));
        }
      }, 500);
    });
  };

  const searchPlaces = async (keyword, location) => {
    // Simulación de búsqueda con datos de ejemplo
    const mockBusinesses = {
      'restaurante': [
        { id: '1', name: 'Restaurante El Buen Sabor', address: 'Calle Mayor 123, Madrid', website: 'https://ejemplo.com', phone: '+34 91 234 5678', rating: 4.5 },
        { id: '2', name: 'Pizzería Napoli', address: 'Avenida Central 45, Madrid', website: '', phone: '+34 91 876 5432', rating: 4.2 },
        { id: '3', name: 'Café Central', address: 'Plaza España 12, Madrid', website: 'https://cafecentral.es', phone: '+34 91 345 6789', rating: 4.8 },
        { id: '4', name: 'Hamburguesería Deluxe', address: 'Calle Serrano 89, Madrid', website: '', phone: '+34 91 567 8901', rating: 4.0 },
        { id: '5', name: 'Sushi Zen', address: 'Paseo de la Castellana 234, Madrid', website: 'https://sushizen.es', phone: '+34 91 789 0123', rating: 4.6 }
      ],
      'farmacia': [
        { id: '6', name: 'Farmacia San Miguel', address: 'Calle Alcalá 67, Madrid', website: '', phone: '+34 91 234 5678', rating: 4.3 },
        { id: '7', name: 'Farmacia Cruz Verde', address: 'Gran Vía 123, Madrid', website: 'https://cruzverdefarmacia.com', phone: '+34 91 345 6789', rating: 4.1 },
        { id: '8', name: 'Farmacia Central', address: 'Puerta del Sol 5, Madrid', website: '', phone: '+34 91 456 7890', rating: 4.4 }
      ],
      'dentista': [
        { id: '9', name: 'Clínica Dental Sonrisa', address: 'Calle Goya 45, Madrid', website: 'https://clinicasonrisa.com', phone: '+34 91 567 8901', rating: 4.7 },
        { id: '10', name: 'Dental Care Madrid', address: 'Calle Velázquez 78, Madrid', website: '', phone: '+34 91 678 9012', rating: 4.5 },
        { id: '11', name: 'Ortodental', address: 'Avenida América 234, Madrid', website: 'https://ortodental.es', phone: '+34 91 789 0123', rating: 4.2 }
      ],
      'hotel': [
        { id: '12', name: 'Hotel Gran Madrid', address: 'Gran Vía 123, Madrid', website: 'https://granmadrid.com', phone: '+34 91 234 5678', rating: 4.4 },
        { id: '13', name: 'Hostal Sol', address: 'Puerta del Sol 15, Madrid', website: '', phone: '+34 91 345 6789', rating: 4.0 },
        { id: '14', name: 'Hotel Boutique Central', address: 'Calle Mayor 89, Madrid', website: 'https://boutique-central.com', phone: '+34 91 456 7890', rating: 4.8 }
      ]
    };

    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const keywordLower = keyword.toLowerCase();
        let results = [];

        // Buscar coincidencias parciales
        for (const [category, businesses] of Object.entries(mockBusinesses)) {
          if (category.includes(keywordLower) || keywordLower.includes(category)) {
            results = [...results, ...businesses];
          }
        }

        // Si no hay coincidencias exactas, buscar en todos los nombres
        if (results.length === 0) {
          for (const businesses of Object.values(mockBusinesses)) {
            results = [...results, ...businesses.filter(b => 
              b.name.toLowerCase().includes(keywordLower)
            )];
          }
        }

        // Si aún no hay resultados, mostrar algunos ejemplos
        if (results.length === 0) {
          results = [...mockBusinesses.restaurante.slice(0, 3), ...mockBusinesses.farmacia.slice(0, 2)];
        }

        // Agregar URLs de Google Maps
        const businessesWithMaps = results.slice(0, 20).map(business => ({
          ...business,
          mapsUrl: `https://www.google.com/maps/search/${encodeURIComponent(business.name + ' ' + business.address)}`
        }));

        resolve(businessesWithMaps);
      }, 1000);
    });
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!keyword.trim() || !zipCode.trim()) {
      setError('Por favor ingresa tanto la palabra clave como el código postal');
      return;
    }

    if (!apiKey || apiKey === 'tu_api_key_aqui') {
      setError('Por favor configura tu API Key real de Google Places en el archivo .env');
      return;
    }

    if (!window.google) {
      setError('API Key inválida o Google Maps no se cargó correctamente. Verifica que tu API Key tenga permisos para Maps JavaScript API, Places API y Geocoding API.');
      return;
    }

    setLoading(true);
    setError('');
    setBusinesses([]);

    try {
      console.log('Geocodificando código postal:', zipCode.trim());
      const location = await geocodeZipCode(zipCode.trim());
      console.log('Ubicación encontrada:', location);

      console.log('Buscando lugares para:', keyword.trim());
      const results = await searchPlaces(keyword.trim(), location);
      console.log('Resultados encontrados:', results.length);

      setBusinesses(results);
    } catch (err) {
      console.error('Error en búsqueda:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const filteredBusinesses = businesses.filter(business => {
    const matchesName = business.name.toLowerCase().includes(nameFilter.toLowerCase());
    const matchesRating = (() => {
      switch (ratingFilter) {
        case '4+': return business.rating >= 4;
        case '3.5+': return business.rating >= 3.5;
        case '3+': return business.rating >= 3;
        default: return true;
      }
    })();

    return matchesName && matchesRating;
  });

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-center h-16">
            <h1 className="text-2xl font-bold text-gray-900">Buscador de Negocios</h1>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Search Form */}
        <div className="bg-white rounded-lg shadow-sm border p-6 mb-6">
          <form onSubmit={handleSearch} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="relative">
                <label htmlFor="keyword" className="block text-sm font-medium text-gray-700 mb-2">
                  Palabra clave
                </label>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <input
                    id="keyword"
                    type="text"
                    value={keyword}
                    onChange={(e) => setKeyword(e.target.value)}
                    placeholder="Ej: restaurante, dentista, tienda..."
                    className="pl-10 w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
              </div>

              <div className="relative">
                <label htmlFor="zipcode" className="block text-sm font-medium text-gray-700 mb-2">
                  Código postal
                </label>
                <div className="relative">
                  <MapPin className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <input
                    id="zipcode"
                    type="text"
                    value={zipCode}
                    onChange={(e) => setZipCode(e.target.value)}
                    placeholder="Ej: 28001, 08001..."
                    className="pl-10 w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
              </div>
            </div>

            <div className="flex justify-center">
              <button
                type="submit"
                disabled={loading}
                className="inline-flex items-center gap-2 px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200"
              >
                {loading ? (
                  <Loader className="w-4 h-4 animate-spin" />
                ) : (
                  <Search className="w-4 h-4" />
                )}
                {loading ? 'Buscando...' : 'Buscar'}
              </button>
            </div>
          </form>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <p className="text-red-800">{error}</p>
          </div>
        )}

        {/* Filters */}
        {businesses.length > 0 && (
          <div className="bg-white rounded-lg shadow-sm border p-4 mb-6">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="flex items-center gap-2 text-gray-700 hover:text-gray-900 transition-colors duration-200"
            >
              <Filter className="w-4 h-4" />
              Filtros
            </button>

            {showFilters && (
              <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="nameFilter" className="block text-sm font-medium text-gray-700 mb-2">
                    Filtrar por nombre
                  </label>
                  <input
                    id="nameFilter"
                    type="text"
                    value={nameFilter}
                    onChange={(e) => setNameFilter(e.target.value)}
                    placeholder="Buscar por nombre..."
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label htmlFor="ratingFilter" className="block text-sm font-medium text-gray-700 mb-2">
                    Filtrar por calificación
                  </label>
                  <select
                    id="ratingFilter"
                    value={ratingFilter}
                    onChange={(e) => setRatingFilter(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">Todas las calificaciones</option>
                    <option value="4+">4+ estrellas</option>
                    <option value="3.5+">3.5+ estrellas</option>
                    <option value="3+">3+ estrellas</option>
                  </select>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Results */}
        {filteredBusinesses.length > 0 && (
          <div className="bg-white rounded-lg shadow-sm border overflow-hidden">
            <div className="px-6 py-4 border-b bg-gray-50">
              <h2 className="text-lg font-semibold text-gray-900">
                Resultados ({filteredBusinesses.length})
              </h2>
            </div>

            {/* Desktop Table */}
            <div className="hidden md:block overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Nombre
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Dirección
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Contacto
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Calificación
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Acciones
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredBusinesses.map((business) => (
                    <tr key={business.id} className="hover:bg-gray-50 transition-colors duration-200">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="font-medium text-gray-900">{business.name}</div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm text-gray-900">{business.address}</div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="space-y-1">
                          {business.website && (
                            <div className="flex items-center gap-1">
                              <Globe className="w-3 h-3 text-gray-400" />
                              <a
                                href={business.website}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="text-sm text-blue-600 hover:text-blue-800 transition-colors duration-200"
                              >
                                Sitio web
                              </a>
                            </div>
                          )}
                          {business.phone && (
                            <div className="flex items-center gap-1">
                              <Phone className="w-3 h-3 text-gray-400" />
                              <span className="text-sm text-gray-900">{business.phone}</span>
                            </div>
                          )}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {business.rating > 0 ? (
                          <StarRating rating={business.rating} />
                        ) : (
                          <span className="text-sm text-gray-500">Sin calificación</span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <a
                          href={business.mapsUrl}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="inline-flex items-center gap-1 text-sm text-blue-600 hover:text-blue-800 transition-colors duration-200"
                        >
                          <MapPin className="w-3 h-3" />
                          Ver en Maps
                          <ExternalLink className="w-3 h-3" />
                        </a>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Mobile Cards */}
            <div className="md:hidden divide-y divide-gray-200">
              {filteredBusinesses.map((business) => (
                <div key={business.id} className="p-4 space-y-3">
                  <div>
                    <h3 className="font-medium text-gray-900">{business.name}</h3>
                    <p className="text-sm text-gray-600 mt-1">{business.address}</p>
                  </div>

                  {business.rating > 0 && (
                    <StarRating rating={business.rating} />
                  )}

                  <div className="space-y-2">
                    {business.website && (
                      <div className="flex items-center gap-2">
                        <Globe className="w-4 h-4 text-gray-400" />
                        <a
                          href={business.website}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-sm text-blue-600 hover:text-blue-800 transition-colors duration-200"
                        >
                          Sitio web
                        </a>
                      </div>
                    )}

                    {business.phone && (
                      <div className="flex items-center gap-2">
                        <Phone className="w-4 h-4 text-gray-400" />
                        <span className="text-sm text-gray-900">{business.phone}</span>
                      </div>
                    )}

                    <div className="flex items-center gap-2">
                      <MapPin className="w-4 h-4 text-gray-400" />
                      <a
                        href={business.mapsUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center gap-1 text-sm text-blue-600 hover:text-blue-800 transition-colors duration-200"
                      >
                        Ver en Google Maps
                        <ExternalLink className="w-3 h-3" />
                      </a>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {businesses.length > 0 && filteredBusinesses.length === 0 && (
          <div className="bg-white rounded-lg shadow-sm border p-8 text-center">
            <p className="text-gray-500">No se encontraron resultados con los filtros aplicados.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
